(()=>{const e=JSON.parse(sessionStorage.getItem("recipes")),i=new URLSearchParams(window.location.search).get("id"),n=e.find(e=>e.slug===i),s=document.querySelector(".recipe-detail__name"),t=document.querySelector(".recipe-detail__image"),r=document.querySelector(".recipe-detail__title"),c=document.querySelector(".recipe-detail__desc"),o=document.querySelector(".recipe-detail__prep-details"),p=document.querySelector(".recipe-detail__ingredients-list"),a=document.querySelector(".recipe-detail__instructions-list"),l=document.querySelector(".recipe-detail__suggestions-box");s.textContent=n.title;const g=`\n    <source srcset=${n.image.large} media="(min-width: 48em)" type="image/webp" />    \n    <img src=${n.image.small} />\n  `;t.innerHTML=g,r.textContent=n.title,c.textContent=n.overview;const m=`\n   <div>\n    <img src="./img/svgicons/icon-servings.svg" class="recipe-detail__prep-icon" />\n    <span>Servings: ${n.servings}</span>\n  </div>\n  <div>\n    <img src="./img/svgicons/icon-prep-time.svg" class="recipe-detail__prep-icon" />\n    <span>Prep: ${n.prepMinutes} ${n.prepMinutes>1?"mins":"min"}</span>\n  </div>\n  <div>\n    <img src="./img/svgicons/icon-cook-time.svg" class="recipe-detail__prep-icon" />\n    <span>Cook: ${n.cookMinutes} ${n.cookMinutes>1?"mins":"min"}</span>\n  </div>\n  `;o.innerHTML=m;const d=n.ingredients.map(e=>`\n       <li> \n        <article>\n          <svg class="recipe-detail__ingredients-icon">\n           <use href="./img/svgsprite/sprite.symbol.svg#icon-bullet-point" />\n          </svg>\n         <p>${e}</p>\n        </article>\n      </li>\n`);p.innerHTML=d.join("");const v=n.instructions.map(e=>`\n       <li> \n        <article>\n          <svg class="recipe-detail__instructions-icon">\n           <use href="./img/svgsprite/sprite.symbol.svg#icon-bullet-point" />\n          </svg>\n         <p>${e}</p>\n        </article>\n      </li>\n`);a.innerHTML=v.join("");const u=e.filter(e=>e.id!==n.id),_=new Set;for(;_.size<3&&_.size<u.length;){const e=Math.floor(Math.random()*u.length);_.add(u[e])}const h=[..._].map(e=>`<article class="recipe">\n      <picture class="recipe__image">\n          <source\n            srcset=${e.image.large}\n            type="image/webp"\n            media="(min-width: 48em)"\n          />\n          <img\n            loading="lazy"\n            src=${e.image.small}\n            alt="A Smiling family showing off by holding fresh tomatoes, greens, paprika, and lemon in their modern kitchen"\n          />\n      </picture>\n      <div class="recipe__info">\n          <h3 class="recipe__title">${e.title}</h3>\n          <p class="recipe__desc">${e.overview}</p>\n      </div>\n      <div class="recipe__preparation">\n        <div>\n         <img src="./img/svgicons/icon-servings.svg" />\n         <p>Servings: ${e.servings}</p>\n       </div>\n\n       <div>\n         <img src="./img/svgicons/icon-prep-time.svg" />\n         <p>Prep: ${e.prepMinutes}  ${e.prepMinutes>1?"mins":"min"}</p>\n       </div>\n      <div>\n         <img src="./img/svgicons/icon-cook-time.svg" />\n         <p>Cook: ${e.cookMinutes} ${e.cookMinutes>1?"mins":"min"} </p>\n       </div>\n       </div>\n      <a href="./recipe-detail.html?id=${e.slug}" class="recipe__link button">View Recipe</a>\n    </article>\n  `);l.innerHTML=h.join("")})();