(()=>{const e=JSON.parse(sessionStorage.getItem("recipes")),i=new URLSearchParams(window.location.search).get("id"),n=e.find(e=>e.slug===i),t=document.querySelector(".recipe-detail__name"),s=document.querySelector(".recipe-detail__image"),r=document.querySelector(".recipe-detail__title"),c=document.querySelector(".recipe-detail__desc"),o=document.querySelector(".recipe-detail__prep-details"),l=document.querySelector(".recipe-detail__ingredients-list"),p=document.querySelector(".recipe-detail__instructions-list");t.textContent=n.title;const a=`\n    <source srcset=.${n.image.large} media="(min-width: 48em)" type="image/webp" />    \n    <img src=.${n.image.small} />\n  `;s.innerHTML=a,r.textContent=n.title,c.textContent=n.overview;const g=`\n   <div>\n    <img src="../img/svgicons/icon-servings.svg" class="recipe-detail__prep-icon" />\n    <span>Servings: ${n.servings}</span>\n  </div>\n  <div>\n    <img src="../img/svgicons/icon-prep-time.svg" class="recipe-detail__prep-icon" />\n    <span>Prep: ${n.prepMinutes} ${n.prepMinutes>1?"mins":"min"}</span>\n  </div>\n  <div>\n    <img src="../img/svgicons/icon-cook-time.svg" class="recipe-detail__prep-icon" />\n    <span>Cook: ${n.cookMinutes} ${n.cookMinutes>1?"mins":"min"}</span>\n  </div>\n  `;o.innerHTML=g;const m=n.ingredients.map(e=>`\n       <li> \n        <article>\n          <svg class="recipe-detail__ingredients-icon">\n           <use href="../img/svgsprite/sprite.symbol.svg#icon-bullet-point" />\n          </svg>\n         <p>${e}</p>\n        </article>\n      </li>\n`);l.innerHTML=m.join("");const d=n.instructions.map(e=>`\n       <li> \n        <article>\n          <svg class="recipe-detail__instructions-icon">\n           <use href="../img/svgsprite/sprite.symbol.svg#icon-bullet-point" />\n          </svg>\n         <p>${e}</p>\n        </article>\n      </li>\n`);p.innerHTML=d.join("")})();