(()=>{const e=document.querySelector(".recipes__container"),i=document.querySelectorAll(".recipes__select"),n=JSON.parse(sessionStorage.getItem("recipes"));[...i].forEach(e=>{e.addEventListener("click",i=>{"false"===e.getAttribute("aria-expanded")?e.setAttribute("aria-expanded","true"):e.setAttribute("aria-expanded","false")})});const s=n.map(e=>`\n    <article class="recipe">\n      <picture class="recipe__image">\n          <source\n            srcset=${e.image.large}\n            type="image/webp"\n            media="(min-width: 48em)"\n          />\n          <img\n            loading="lazy"\n            src=${e.image.small}\n            alt="A Smiling family showing off by holding fresh tomatoes, greens, paprika, and lemon in their modern kitchen"\n          />\n      </picture>\n      <div class="recipe__info">\n          <h3 class="recipe__title">${e.title}</h3>\n          <p class="recipe__desc">${e.overview}</p>\n      </div>\n      <div class="recipe__preparation">\n        <div>\n         <img src="./img/svgicons/icon-servings.svg" />\n         <p>Servings: ${e.servings}</p>\n       </div>\n\n       <div>\n         <img src="./img/svgicons/icon-prep-time.svg" />\n         <p>Prep: ${e.prepMinutes}  ${e.prepMinutes>1?"mins":"min"}</p>\n       </div>\n      <div>\n         <img src="./img/svgicons/icon-cook-time.svg" />\n         <p>Cook: ${e.cookMinutes} ${e.cookMinutes>1?"mins":"min"} </p>\n       </div>\n       </div>\n      <a href=${e.slug} class="recipe__link button">View Recipe</a>\n    </article>\n`);e.innerHTML=s.join("")})();